import React, { Component } from "react";
import gql from "graphql-tag";
import { client } from "../modules/apolloClient";

const siteQuery = gql`
  query($siteUrl: String) {
    sites(where: { name: { equals: $siteUrl } }) {
      id
      language
      languageLabel
      name
      niche
      logotype {
        url
      }
      providers(first: 10) {
        name
        rating
        slug
      }
    }
  }
`;

export default class IndexPage extends Component {
  static async getInitialProps(ctx) {
    let { data: {sites} } = await client.query({
      query: siteQuery,
      variables: {
        siteUrl: ctx.req.headers["wdn-site-url"]
      }
    });

    return { site: sites[0] };
  }

  render() {
    return (
      <div>
        <pre>{JSON.stringify(this.props.site, null, 2)}</pre>
      </div>
    );
  }
}
