const siteQuery = gql`
  query {
    sites(where: {name: {equals: "livecasinorank.se"}}){
      id
      language
      languageLabel
      name
      niche
      logotype {
        url
      }
    }
  }
`


export default class IndexPage extends Component {

  async getInitialProps(ctx){
    let {data} = await client.query({
      query: siteQuery
    })
    console.log(data)

